************************************sub help{   local($contents) = "";   local($back_tag) = "<a href=\"$ENV{'HTTP_REFERER'}\">" .                      "Back to the Discussion</a>";   local($base_tag) = "<base href=\"$image_base_url/\">";   open(HF, "$help_file");   while(<HF>){ $contents .= $_; }   close(HF);     $contents =~ s/<!--BASE-->/$base_tag/;   $contents =~ s/<!--BACK-->/$back_tag/;   print $contents;}# ************************************************************************1;  # Must return a true value to be required!# ************************************************************************# End of Module: Help # ************************************************************************